import { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";

const textos = { color: "violet" };
const info = { textAlign: "left", fontSize: "24px" };
const image = { borderRadius: "10px", border: "2px solid darkslateblue" };
const divPrincipal = {
  color: "white",
  display: "flex",
  justifyContent: "space-around",
  width: "80%",
  margin: "auto",
  marginTop: "75px",
};
const styleButton = {
  backgroundColor: "pink",
  color: "purple",
  border: "0px",
  borderRadius: "5px",
  fontWeight: "bold",
  fontSize: "15px",
  padding: "10px",
  marginTop: "35px",
  cursor: "pointer",
};

export default function Detail() {
  const { detailId } = useParams();
  const [char, setCharacter] = useState({
    name: "",
    status: "",
    specie: "",
    gender: "",
    origin: "",
    image: "",
  });

  const navigate = useNavigate();

  useEffect(() => {
    fetch(`https://localhost:3001/rickandmorty/detail/${detailId}`)
      .then((response) => response.json())
      .then((char) => {
        if (char.name) {
          setCharacter({
            name: char.name,
            status: char.status,
            specie: char.specie,
            gender: char.specie,
            origin: char.origin.name,
            image: char.image,
          });
        } else {
          alert("No hay personajes con ese ID");
        }
      })
      .catch((err) => {
        alert("No hay personajes con ese ID");
      });
  }, [detailId]);

  return (
    <div>
      <div style={divPrincipal}>
        <div style={info}>
          {char.name && (
            <p>
              <b style={textos}>Name:</b> &#160; {char.name}
            </p>
          )}
          {char.status && (
            <p>
              <b style={textos}>Status:</b> &#160; {char.status}
            </p>
          )}
          {char.specie && (
            <p>
              <b style={textos}>Specie:</b> &#160; {char.specie}
            </p>
          )}
          {char.gender && (
            <p>
              <b style={textos}>Gender:</b> &#160; {char.gender}
            </p>
          )}
          {char.origin && (
            <p>
              <b style={textos}>Origin:</b> &#160; {char.origin}
            </p>
          )}
        </div>
        <img style={image} src={char.image} alt=" "/>
      </div>
      <button style={styleButton} onClick={() => navigate('/home')}>Back To Home</button>
    </div>
  );
}
